CustomField.formula:
TEXT(
FLOOR( (IF(ISPICKVAL(Status, "Resolved"), Resolved_Timestamp__c, NOW()) - Functional_User_Assignment_Time__c) )
) & " Days " &

TEXT(
FLOOR( MOD( (IF(ISPICKVAL(Status, "Resolved"), Resolved_Timestamp__c, NOW()) - Functional_User_Assignment_Time__c) * 24, 24 ) )
) & " Hrs " &

TEXT(
FLOOR( MOD( (IF(ISPICKVAL(Status, "Resolved"), Resolved_Timestamp__c, NOW()) - Functional_User_Assignment_Time__c) * 24 * 60, 60 ) )
) & " Mins"
