  IF(
    OR(
      AND(
        (Project_Location__c = "Mumbai"),
        Waiver_Amount__c <= 200000
      ),
      AND(
        (Project_Location__c = "Dombivali"),
        Waiver_Amount__c <= 100000
      )
    ),
    "Level 2",

    IF(
      OR(
        AND(
          (Project_Location__c = "Mumbai"),
          Waiver_Amount__c > 200000
        ),
        AND(
          (Project_Location__c = "Dombivali"),
          Waiver_Amount__c > 100000
        )
      ),
      "Level 3",
