ValidationRule.errorConditionFormula:
AND(
   NOT(ISPICKVAL(CP_Lead_Status__c, "Inactive")),
   OR(
      AND(
         NOT(ISBLANK(RW_Secondary_Mobile_No__c)),
         ISPICKVAL(Country_Code_2__c, "")
      ),
      AND(
         ISBLANK(RW_Secondary_Mobile_No__c),
         NOT(ISPICKVAL(Country_Code_2__c, ""))
      )
   )
)
