ValidationRule.errorConditionFormula:
AND(OR(ISPICKVAL(Call_Rating__c,'Hot (4)'),
ISPICKVAL(Call_Rating__c,'Warm (3)'),
ISPICKVAL(Call_Rating__c,'Cold (2)')),
ISPICKVAL(Task_Type__c, 'Site Visit'),
ISBLANK(Next_Action_Date__c),
				/*Added by Prashant to avoid the validation for Revisit-- START*/
				NOT(Revisit__c) ,
				/*Added by Prashant to avoid the validation for Revisit-- END*/
$Profile.Name !='System Administrator'
)
